mapper.update:
update down_system
set task_current_running_count = task_current_running_count + 1
where id = #{id}

update down_system
set task_current_running_count = task_current_running_count - 1
where id = #{id}

update down_system_site
set url_last_cache_timeout_job_time = current_timestamp
where id = #{id}
