mapper.update:
update proxy
set current_use_count = current_use_count + 1
where id = #{id}

update proxy
set current_use_count = current_use_count - 1
where id = #{id}

mapper.select:
select * from proxy p where -- enabled p.enable_status = 1 -- limit ip
and p.ip = #{ip}
-- max concurrency and p.current_use_count < p.max_use_count -- over max block count and p.block_current_count < p.block_max_count -- not blocked and p.block_timeout_time < current_timestamp -- not in block map and not exists ( select ip from site_ip_block_map bm where bm.site_id = #{siteId} and bm.ip = p.ip and bm .block_timeout_time > current_timestamp ) -- not in delay map and not exists ( select 1 from site_ip_delay_map dm where dm.site_id = #{siteId} and dm.ip = p.ip and dm.delay_timeout_time > current_timestamp ) order by p.current_use_count/p.max_use_count asc limit 0,1
